import "./interfaces/IPortalToken.sol";
import {ILiquidityPool} from "./interfaces/ILiquidityPool.sol";

/**
 * @title LiquidityPool
 * @dev Manages the registration and maintanence of LiquidityPool
 * on the Portal Chain.
 * Only the owner (Portal Foundation) can perform these actions.
 * It interacts with LiquidityPool contracts and maintains 
 * LiquidityPool-related mappings.
 */
contract LiquidityPool is ILiquidityPool {
    /**
     * @notice Address of the PORTAL Token contract on portal chain
     */
    IPortalToken public tokenContract;

    /**
     * @notice Mapping of liquidityPoolId to LiquidityPool
     */
    mapping(bytes32 => LiquidityPool) private poolsById;

    /**
     * @notice Mapping of pair details to liquidityPoolId
     */
    mapping(address => mapping(address => mapping(uint => bytes32)))
        private poolsIdsByAssets; // Map asset pairs to their ID for quick lookup

     /**
     * @notice list of all asset pairs
     */
    LiquidityPool[] private pools;

        /**
     * @dev Initializes the contract setting the portal token address
     * @param portalTokenContractAddress The address of the portalToken 
     * on the portal chain
     */
    constructor(address portalTokenContractAddress) {
        tokenContract = IPortalToken(portalTokenContractAddress);
    }

        /**
     * @dev Modifier that restricts the execution of the function to 
     * only the current owners of the contract.
     * It reverts the transaction if the condition is not met.
     */
    modifier onlyOwner() {
        if (tokenContract.owner() != msg.sender) {
            revert NotOwner(msg.sender);
        }
        _;
    }

    /**
     * @inheritdoc ILiquidityPool
     */
    function createLiquidityPool(
        LiquidityPool memory pool
    ) external onlyOwner{
        address baseAsset = pool.baseAsset;
        address quoteAsset = pool.quoteAsset;
        (address asset0, address asset1) = baseAsset < quoteAsset
        ? (baseAsset, quoteAsset)
        : (quoteAsset, baseAsset);

        bytes32 liquidityPoolId = sha256(abi.encodePacked(
            asset0, asset1, pool.poolFee
        ));
        require(
            poolsById[liquidityPoolId].baseAsset == address(0),
            "LiquidityPool already exists"
        );
        pool.id = liquidityPoolId;
        pool.baseAsset = baseAsset;
        pool.quoteAsset = quoteAsset;

        poolsById[liquidityPoolId] = pool;
        poolsIdsByAssets[asset0][asset1][pool.poolFee] = liquidityPoolId;
        pools.push(poolsById[liquidityPoolId]);
        emit LiquidityPoolCreated(liquidityPoolId);
    }

        /**
     * @notice function to retrieve liquidityPoolId from pair details
     * @param sellAsset Trader's sell asset address
     * @param buyAsset Trader's buy asset address
     * @param poolFee Fee of the trading pool
     * @return liquidityPoolId liquidityPoolId of the pool
     */
    function retrievePoolId(
        address sellAsset,
        address buyAsset,
        uint poolFee
    )
        external
        view
        returns (bytes32)
    {
        (address asset0, address asset1) = sellAsset < buyAsset
        ? (sellAsset, buyAsset)
        : (buyAsset, sellAsset);

        bytes32 liquidityPoolId = poolsIdsByAssets[asset0][asset1][poolFee];
        require(liquidityPoolId != bytes32(0), "Asset pair not registered");

        return liquidityPoolId;
    }

    /**
     * @inheritdoc ILiquidityPool
     */
    function retrievePool(
        bytes32 id
    ) external view override returns (LiquidityPool memory) {
        LiquidityPool memory pool = poolsById[id];
        require(pool.baseAsset != address(0), "POOL_NOT_FOUND");
        return pool;
    }

    /**
     * @inheritdoc ILiquidityPool
     */
    function listPools() external view returns (LiquidityPool[] memory) {
        return pools;
    }
}
