pragma solidity ^0.8.20;
import "./interfaces/IPortalToken.sol";
import {ILiquidityProvider} from "./interfaces/ILiquidityProvider.sol";

/**
 * @title Liquidity Provider contract
 * @dev Implementation of the Liquidity Provider which manages liquidity management and dispatching funds for the swap
 */
contract LiquidityProvider is ILiquidityProvider {
    /**
     * @notice Address on the PORTAL Token contract
     */
    IPortalToken public tokenContract;

    /**
     * @notice Stake percentage in basis points (100 basis points = 1%)
     */
    uint public stakePercentage;

    /**
     * @notice Initialize the contract with the PORTAL Token address and stake percentage
     * @param _tokenAddress Address of the Portal Token contract
     */
    constructor(address _tokenAddress) {
        tokenContract = IPortalToken(_tokenAddress); //Loading the portal contract from portal token address
        stakePercentage = 300; // Initializing with a 3% stake
    }

    /**
     * @notice Modifier to check if the caller is the owner of the token contract
     */
    modifier onlyOwner() {
        if (tokenContract.owner() != msg.sender) {
            revert NotOwner(msg.sender);
        }
        _;
    }

    /**
     * @notice mapping between liquidity asset, liquidity owner and the liquidity amount
     */
    mapping(address => mapping(address => uint)) public liquidities;

    /**
     * @notice mapping between liquidity asset, liquidity owner and the stake amount
     */
    mapping(address => mapping(address => uint)) public stakes;



    /**
     * Function to deposit liquidity into the contract
     * @param depositAsset Address of the asset to be deposited
     * @param depositAmount Amount of the asset to be deposited
     */
    function depositLiquidity(
        address depositAsset,
        uint depositAmount,
        address depositOwner
    ) public payable {
        // Handle ETH and ERC20 transfers differently
        if (depositAsset == address(0x0)) {
            if (msg.value != depositAmount) {
                revert IncorrectETHAmount(depositAmount);
            }
        } else {
            // Transfer ERC20 tokens from sender to contract
            IERC20(depositAsset).transferFrom(
                msg.sender,
                address(this),
                depositAmount
            );
        }
        stakes[depositAsset][depositOwner] +=
            (depositAmount * stakePercentage) /
            10000;
        liquidities[depositAsset][depositOwner] +=
            depositAmount -
            (depositAmount * stakePercentage) /
            10000;

        emit LiquidityDeposited(depositOwner, depositAsset, depositAmount);
    }

    /**
     * @notice mapping between penalty asset and the penalty amount
     */
    mapping(address => uint) public penalties;

    /**
     * Function to penalize the user for any misconduct
     * @param user Address of the user to be penalized
     * @param penaltyAsset Address of the asset to be penalized
     * @param penaltyAmount Amount of the asset to be penalized
     */
    function lpPenalty(
        address user,
        address penaltyAsset,
        uint penaltyAmount
    ) public onlyOwner {
        require(
            penaltyAmount <= stakes[penaltyAsset][user],
            "penalty should be less than stake"
        );
        stakes[penaltyAsset][user] -= penaltyAmount;
        penalties[penaltyAsset] += penaltyAmount;
    }

    /**
     * Function to withdraw penalties
     * @param withdrawAsset Address of the asset to be withdrawn
     * @param withdrawAmount Amount of the asset to be withdrawn
     */
    function withdrawPenalties(
        address withdrawAsset,
        uint withdrawAmount
    ) public onlyOwner {
        require(
            withdrawAmount <= penalties[withdrawAsset],
            "withdrawAmount should be less than penalties"
        );
        penalties[withdrawAsset] -= withdrawAmount;
        if (withdrawAsset == address(0x0)) {
            // Handle ETH withdrawal
            payable(msg.sender).transfer(withdrawAmount);
        } else {
            // Handle ERC20 token withdrawal
            IERC20(withdrawAsset).transfer(msg.sender, withdrawAmount);
        }
    }

    /**
     * @notice mapping between liquidity asset, liquidity owner and the withdrawal info
     */
    mapping(address => mapping(address => WithdrawalInfo))
        public liquidityTimelocks;

    /**
     * Function to remove liquidity from the contract
     * @param withdrawalAsset Address of the asset to be withdrawn
     * @param withdrawalAmount Amount of the asset to be withdrawn
     */
    function removeLiquidity(
        address withdrawalAsset,
        uint withdrawalAmount
    ) public {
        require(
            liquidities[withdrawalAsset][msg.sender] >= withdrawalAmount,
            "Insufficient liquidity"
        );
        WithdrawalInfo storage info = liquidityTimelocks[withdrawalAsset][
            msg.sender
        ];
        require(
            info.unlockTime < block.timestamp,
            "Wait till notice completion"
        );
        require(
            withdrawalAmount <= info.maxWithdrawalAmount,
            "Withdrawal amount exceeds limit"
        );
        // Decrement the max withdrawal amount by the amount being withdrawn
        info.maxWithdrawalAmount -= withdrawalAmount;
        liquidities[withdrawalAsset][msg.sender] -= withdrawalAmount;

        uint stakeRequired = ((liquidities[withdrawalAsset][msg.sender] +
            stakes[withdrawalAsset][msg.sender]) * stakePercentage) / 10000;

        uint stakeRefund = 0;
        if (stakeRequired < stakes[withdrawalAsset][msg.sender]) {
            stakeRefund = stakes[withdrawalAsset][msg.sender] - stakeRequired;
            stakes[withdrawalAsset][msg.sender] = stakeRequired;
        }

        if (withdrawalAsset == address(0x0)) {
            // Handle ETH withdrawal
            payable(msg.sender).transfer(withdrawalAmount + stakeRefund);
        } else {
            // Handle ERC20 token withdrawal
            IERC20(withdrawalAsset).transfer(
                msg.sender,
                withdrawalAmount + stakeRefund
            );
        }
        emit LiquidityRemoved(
            msg.sender,
            withdrawalAsset,
            withdrawalAmount + stakeRefund
        );
    }

    /**
     * Function to give notice to remove liquidity from the contract
     * @param withdrawalAsset Address of the asset to be withdrawn
     * @param maxWithdrawalAmount Maximum amount of the asset to be withdrawn
     */
    function removeLiquidityNotice(
        address withdrawalAsset,
        uint maxWithdrawalAmount
    ) public {
        liquidityTimelocks[withdrawalAsset][msg.sender] = WithdrawalInfo({
            unlockTime: block.timestamp + 2 days, // 2 days notice to remove liquidity
            maxWithdrawalAmount: maxWithdrawalAmount
        });

        emit LiquidityRemovalNotice(
            msg.sender,
            withdrawalAsset,
            maxWithdrawalAmount,
            block.timestamp + 2 days
        );
    }

    /**
     * @notice mapping between swapId and the swap order details
     */
    mapping(bytes32 => Order) public swaps;

    /**
     * Function to create an invoice for a swap
     * @param swapId Identifier for the swap
     * @param sellAsset Asset address to be sold (0x0 for ETH)
     * @param sellAmount Amount of the asset to be sold
     * @param counterParty Address of the other party in trade
     */
    function createInvoice(
        bytes32 swapId,
        address sellAsset,
        uint sellAmount,
        address counterParty
    ) public {
        require(swaps[swapId].sellAmount == 0, "swapId already exists");
        require(
            liquidities[sellAsset][msg.sender] >= sellAmount,
            "Add more liquidity"
        );
        liquidities[sellAsset][msg.sender] -= sellAmount;
        swaps[swapId] = Order({
            swapOwner: msg.sender, // Address of the trader initiating the swap
            sellAsset: sellAsset, // Asset address to be sold (0x0 for ETH)
            sellAmount: sellAmount, // Amount of the asset to be sold
            swapCreation: block.timestamp, // Timestamp of swap creation
            counterParty: counterParty // Address of the other party in trade
        });
        emit InvoiceCreated(
            swapId,
            msg.sender,
            counterParty,
            swaps[swapId].sellAsset,
            swaps[swapId].sellAmount
        );
    }

    /**
     * Function to settle an invoice
     * @param secret Secret to unlock the swap
     * @param swapId Identifier for the swap
     */
    function settleInvoice(bytes32 secret, bytes32 swapId) public {
        // Ensure the swap order exists and has a sell amount greater than zero
        require(swaps[swapId].sellAmount > 0, "Invalid secret for withdrawal");
        address payee;

        if (swaps[swapId].swapCreation + 2 days >= block.timestamp) {
            require(
                swapId == sha256(abi.encodePacked(secret)),
                "swapId must be matched with secret"
            );
            payee = swaps[swapId].counterParty;
        } else {
            payee = swaps[swapId].swapOwner;
        }
        // transfer the asset to the payee
        if (swaps[swapId].sellAsset == address(0x0)) {
            payable(payee).transfer(swaps[swapId].sellAmount);
        } else {
            IERC20 erc20 = IERC20(swaps[swapId].sellAsset);
            erc20.transfer(payee, swaps[swapId].sellAmount);
        }
        // Emit an event for successful invoice settlement
        emit InvoiceSettled(
            swapId,
            secret,
            payee,
            swaps[swapId].sellAsset,
            swaps[swapId].sellAmount
        );
        delete swaps[swapId];
    }
}
