/**
 * @title ILiquidityProvider
 * @notice Interface for the LiquidityProvider contract deployed on the Ethereum network
 */
interface ILiquidityProvider {
    // Define custom errors
    error NotOwner(address sender );
    error OverflowFeeCap(uint amount);
    error IncorrectETHAmount(uint amount);
    error PermissionDeniedToAuthorize(address sender);
    error AlreadyAuthorized(bytes32 swapId);
    error NoExpiredFeeUpdateNotice(uint);

        /**
     * @notice Structure to store the max withdrawal amount by unlock time
     */
    struct WithdrawalInfo {
        uint unlockTime;
        uint maxWithdrawalAmount;
    }

        /**
     * @notice Structure to store the swap order details
     * @param swapOwner Address of the trader initiating the swap
     * @param sellAsset Asset address to be sold (0x0 for ETH)
     * @param sellAmount Amount of the asset to be sold
     * @param swapCreation Timestamp of swap creation
     * @param counterParty Address of the other party in trade
     */
    struct Order {
        address swapOwner;
        address sellAsset;
        uint sellAmount;
        uint swapCreation;
        address counterParty;
    }

        /**
     * @notice Event emitted when liquidity is deposited
     * @param liquidityOwner Address of the liquidity provider
     * @param depositAsset Address of the asset deposited
     * @param depositAmount Amount of the asset deposited
     */
    event LiquidityDeposited(
        address indexed liquidityOwner,
        address indexed depositAsset,
        uint depositAmount
    );

        /**
     * Event emitted when notice to liquidity removal is successfully received
     * @param liquidityOwner Address of the liquidity provider
     * @param withdrawalAsset Address of the asset withdrawn
     * @param withdrawalAmount Amount of the asset withdrawn
     * @param noticeCompletion Timestamp of notice completion
     */
    event LiquidityRemovalNotice(
        address indexed liquidityOwner,
        address indexed withdrawalAsset,
        uint withdrawalAmount,
        uint noticeCompletion
    );

    /**
     * @notice Event emitted when liquidity is removed
     * @param liquidityOwner Address of the liquidity provider
     * @param withdrawalAsset Address of the asset withdrawn
     * @param withdrawalAmount Amount of the asset withdrawn
        */
    event LiquidityRemoved(
        address indexed liquidityOwner,
        address indexed withdrawalAsset,
        uint withdrawalAmount
    );

        /**
     * Event emitted upon Invoice Creation
     * @param swapId Identifier for the swap
     * @param swapOwner Address of the trader initiating the swap
     * @param counterParty Address of the other party in trade
     * @param sellAsset Asset address to be sold (0x0 for ETH)
     * @param sellAmount Amount of the asset to be sold
     */
    event InvoiceCreated(
        bytes32 indexed swapId,
        address indexed swapOwner,
        address indexed counterParty,
        address sellAsset,
        uint sellAmount
    );

        /**
     * Event emitted upon Invoice Settlement
     * @param swapId Identifier for the swap
     * @param secret Secret to unlock the swap
     * @param counterParty Address of the other party in trade
     * @param sellAsset Asset address to be sold (0x0 for ETH)
     * @param sellAmount Amount of the asset to be sold
     */
    event InvoiceSettled(
        bytes32 indexed swapId,
        bytes32 indexed secret,
        address indexed counterParty,
        address sellAsset,
        uint sellAmount
    );
}
