pragma solidity ^0.8.20;
import "./IUniswapPositionManager.sol";
import "../uniswap/core/contracts/interfaces/IUniswapV3Factory.sol";

/**
 * @title IPoolManager
 * @dev The  Pool Manager interface defines the functions for managing liquidity pools and positions on Uniswap V3.
 * @notice This interface is used to interact with the  Pool Manager contract.
 */
interface IPoolManager {
    /**
     * @dev Struct to represent Uniswap parameters.
     * @param admin The address of the admin.
     * @param factory The address of the Uniswap V3 factory.
     * @param nft The address of the NFT.
     * @param router The address of the router.
     */
    struct UniswapParams {
        address admin;
        address factory;
        address nft;
        address router;
    }

    /**
     * @dev Struct to represent a liquidity position.
     * @param tokenId The ID of the liquidity token.
     * @param liquidity The amount of liquidity.
     */
    struct LiquidityPosition {
        uint tokenId;
        uint128 liquidity;
    }

    /**
     * @dev Struct to represent an LP token.
     * @param addr The address of the LP token.
     * @param amountDesired The desired amount of the LP token.
     * @param amountMin The minimum amount of the LP token.
     */

    struct LpToken {
        address addr;
        uint256 amountDesired;
        uint256 amountMin;
    }

    /**
     * @dev Returns the address of the pool for the given tokens and fee.
     * @param tokenA The address of token A.
     * @param tokenB The address of token B.
     * @param fee The fee of the pool.
     * @return pool The address of the pool.
     */
    function getPool(
        address tokenA,
        address tokenB,
        uint24 fee
    ) external view returns (address pool);

    /**
     * @dev Creates a new pool for the given tokens and fee.
     * @param sellAsset The address of the sell asset.
     * @param buyAsset The address of the buy asset.
     * @param poolFee The fee of the pool.
     */
    function createPool(
        address sellAsset,
        address buyAsset,
        uint24 poolFee
    ) external;

    /**
     * @dev Creates a new liquidity position for the given assets and owner.
     * @param sellAsset The LP token for the sell asset.
     * @param buyAsset The LP token for the buy asset.
     * @param owner The address of the owner.
     * @param poolFee The fee of the pool.
     * @param deadline The deadline for the transaction.
     * @param tickLower The lower tick for the position.
     * @param tickUpper The upper tick for the position.
     */
    function createPosition(
        LpToken calldata sellAsset,
        LpToken calldata buyAsset,
        address owner,
        uint24 poolFee,
        uint deadline,
        int24 tickLower,
        int24 tickUpper
    ) external;

    /**
     * @dev Increases the liquidity of the given position.
     * @param tokenId The ID of the liquidity token.
     * @param sellAsset The LP token for the sell asset.
     * @param buyAsset The LP token for the buy asset.
     * @param deadline The deadline for the transaction.
     */
    function increaseLiquidity(
        uint256 tokenId,
        LpToken calldata sellAsset,
        LpToken calldata buyAsset,
        uint deadline
    ) external;

    /**
     * @dev Decreases the liquidity of the given position.
     * @param owner The address of the owner.
     * @param amount0Min The minimum amount of token 0.
     * @param amount1Min The minimum amount of token 1.
     * @param deadline The deadline for the transaction.
     */
    function decreaseLiquidity(
        address owner,
        uint256 amount0Min,
        uint256 amount1Min,
        uint256 deadline
    ) external;

    /**
     * @dev Executes an exact input single swap.
     * @param tokenIn The address of the input token.
     * @param tokenOut The address of the output token.
     * @param fee The fee of the pool.
     * @param amountIn The amount of the input token.
     * @return amountOut The amount of the output token.
     */
    function exactInputSingle(
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountIn
    ) external payable returns (uint256 amountOut);

    /**
     * @dev Returns the address of the Uniswap V3 factory.
     * @return pool The address of the factory.
     */
    function factory() external view returns (IUniswapV3Factory pool);

    /**
     * @dev Sets the initial sqrt price for the given pool.
     * @param sellToken The address of the sell token.
     * @param buyToken The address of the buy token.
     * @param fee The fee of the pool.
     * @param sqrtPriceX96 The initial sqrt price.
     */
    function setInitialSqrtPrice(
        address sellToken,
        address buyToken,
        uint24 fee,
        uint160 sqrtPriceX96
    ) external;

    /**
     * @dev Returns the current sqrt price for the given pool.
     * @param tokenA The address of token A.
     * @param tokenB The address of token B.
     * @param fee The fee of the pool.
     * @return sqrtPriceX96 The current sqrt price.
     */
    function getPoolPrice(
        address tokenA,
        address tokenB,
        uint24 fee
    ) external view returns (uint160 sqrtPriceX96);

    /**
     * @dev Error thrown when no enabled tick spacing is found for the given pool fee.
     * @param poolFee The pool fee for which no enabled tick spacing was found.
     */
    error NoEnabledTickSpacing(uint poolFee);

    /**
     * @dev Error thrown when no admin is found for the given address.
     * @param sender The address for which no admin was found.
     */
    error NoAdmin(address sender);

    /**
     * @dev Error thrown when no initial sqrt price is set for the given sell and buy assets.
     * @param sellAsset The address of the sell asset.
     * @param buyAsset The address of the buy asset.
     * @param fee The fee of the pool.
     */
    error NoInitialSqrtPrice(address sellAsset, address buyAsset, uint160 fee);
}
