pragma solidity ^0.8.20;

import "./interfaces/IPortalToken.sol";
import {IAssetManagement} from "./interfaces/IAssetManagement.sol";
import {Asset} from "./Asset.sol";
import {IAsset} from "./interfaces/IAsset.sol";

/**
 * @title AssetManagement
 * Manages assets in the Portal ecosystem. Assets in the Portal ecosystem are
 * immutable and may not be updated or deleted once created. The contract
 * maintains a list of all assets created in the order they were created. It
 * also maintains a mapping of assets by their native blockchain name and
 * address to enable quick lookups of assets based on native blockchain details.
 */
contract AssetManagement is IAssetManagement {
    /**
     * @notice Address of the PORTAL Token contract on portal chain
     */
    IPortalToken public portalToken;

    /**
     * @notice Maintains a count of the number of assets on portal chain
     */
    uint256 private assetCount;

    /**
     * @notice List of all assets in the order they were created
     */
    Asset[] private assets;

    /**
     * @notice Mapping to check if an asset already exists
     */
    mapping(string => mapping(string => bool)) private existingAssets;

    /**
     * @notice Assets indexed by native blockchain name and address
     */
    mapping(string => mapping(string => Asset)) private assetsByNativeProps;

    /**
     * Initializes the asset management contract
     * @param _portalToken Address of the PORTAL token contract on portal chain
     */
    constructor(address _portalToken) {
        portalToken = IPortalToken(_portalToken);
    }

    /**
     * Modifier that restricts the execution of the function to only the
     * current owners of the contract. It reverts the transaction if the
     * condition is not met.
     */
    modifier onlyOwner() {
        if (msg.sender != portalToken.owner()) revert NotOwner(msg.sender);
        _;
    }

    /**
     * @inheritdoc IAssetManagement
     */
    function createAsset(IAsset.AssetData calldata _asset) external onlyOwner {
        address id = _asset.id;
        require(id == address(0), "INVALID_ASSET_ID");

        string memory blockchainName = _asset.blockchainName;
        string memory blockchainAddress = _asset.blockchainAddress;
        if (existingAssets[blockchainName][blockchainAddress]) {
            revert AssetExists(blockchainName, blockchainAddress);
        }

        Asset asset = new Asset(_asset);

        assets.push(asset);
        assetCount++;

        existingAssets[blockchainName][blockchainAddress] = true;
        assetsByNativeProps[blockchainName][blockchainAddress] = asset;

        emit AssetCreated(asset.id());
    }

    /**
     * @inheritdoc IAssetManagement
     */
    function retrieveAsset(
        address id
    ) external view returns (IAsset.AssetData memory) {
        return Asset(id).toAssetData();
    }

    /**
     * @inheritdoc IAssetManagement
     */
    function retrieveAssetByNativeProps(
        string memory blockchainName,
        string memory blockchainAddress
    ) external view returns (IAsset.AssetData memory) {
        Asset asset = assetsByNativeProps[blockchainName][blockchainAddress];
        return asset.toAssetData();
    }

    /**
     * @inheritdoc IAssetManagement
     */
    function listAssets() external view returns (IAsset.AssetData[] memory) {
        IAsset.AssetData[] memory _assets = new IAsset.AssetData[](assetCount);
        for (uint256 i = 0; i < assetCount; i++) {
            _assets[i] = assets[i].toAssetData();
        }
        return _assets;
    }
}
