/**
 * @title IAsset
 * Interface for the Asset contract that defines all tradable assets in the
 * Portal ecosystem.
 */
interface IAsset {
    /**
     * Stores the particulars of an asset.
     * @param id Address of the asset on the portal chain
     * @param name Human-readable name of the asset
     * @param symbol Ticker symbol of the asset
     * @param logo Logo of the asset as a SVG string
     * @param blockchainId Chain-ID of the native blockchain
     * @param blockchainName Name of the native blockchain
     * @param blockchainAddress Address of the asset on the native blockchain
     * @param blockchainDecimals Number of decimals used by the asset
     */
    struct AssetData {
        address id;
        string name;
        string symbol;
        string logo;
        uint256 blockchainId;
        string blockchainName;
        string blockchainAddress;
        uint8 blockchainDecimals;
    }
}
