pragma solidity ^0.8.20;

import "./IAsset.sol";
import "../Asset.sol";

/**
 * @title IAssetManagement
 * Interface to manage assets in the Portal ecosystem
 */
interface IAssetManagement {
    /**
     * Creates a new asset.
     * NOTE: This method is only callable by the owner of the contract.
     */
    function createAsset(IAsset.AssetData calldata asset) external;

    /**
     * Retrieves the specified asset
     * @param id The address of the asset on the portal chain
     * @return The asset whose id was provided
     */
    function retrieveAsset(
        address id
    ) external view returns (IAsset.AssetData memory);

    /**
     * Retrieves an asset using native blockchain properties
     * @param blockchainName Name of the native blockchain
     * @param blockchainAddress Address of the asset on the native blockchain
     * @return The asset whose native blockchain properties were provided
     */
    function retrieveAssetByNativeProps(
        string memory blockchainName,
        string memory blockchainAddress
    ) external view returns (IAsset.AssetData memory);

    /**
     * Lists all assets on the portal chain in the order they were created
     * @return The list of all assets in the order they were created
     */
    function listAssets() external view returns (IAsset.AssetData[] memory);

    /**
     * Fires when a new asset is created
     * @param id The address of the asset on the portal chain
     */
    event AssetCreated(address indexed id);

    /**
     * Thrown when the caller is not the owner of the contract
     * @param sender The address of the account calling the function
     */
    error NotOwner(address sender);

    /**
     * Thrown when an asset has already been created
     * @param blockchainName Name of the native blockchain
     * @param blockchainAddress Address of the asset on the native blockchain
     */
    error AssetExists(string blockchainName, string blockchainAddress);
}
