// SPDX-License-Identifier: MIT

/// @title Base64
/// @author Brecht Devos - <brecht@loopring.org>
/// @notice Provides a function for encoding some bytes in base64
library Base64 {
    string internal constant TABLE = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/';

    function encode(bytes memory data) internal pure returns (string memory) {
        if (data.length == 0) return '';

        // load the table into memory
        string memory table = TABLE;

        // multiply by 4/3 rounded up
        uint256 encodedLen = 4 * ((data.length + 2) / 3);

        // add some extra buffer at the end required for the writing
        string memory result = new string(encodedLen + 32);

        assembly {
            // set the actual output length
            mstore(result, encodedLen)

            // prepare the lookup table
            let tablePtr := add(table, 1)

            // input ptr
            let dataPtr := data
            let endPtr := add(dataPtr, mload(data))

            // result ptr, jump over length
            let resultPtr := add(result, 32)

            // run over the input, 3 bytes at a time
            for {} lt(dataPtr, endPtr) {}
            {
               dataPtr := add(dataPtr, 3)

               // read 3 bytes
               let input := mload(dataPtr)

               // write 4 characters
               mstore(resultPtr, shl(248, mload(add(tablePtr, and(shr(18, input), 0x3F)))))
               resultPtr := add(resultPtr, 1)
               mstore(resultPtr, shl(248, mload(add(tablePtr, and(shr(12, input), 0x3F)))))
               resultPtr := add(resultPtr, 1)
               mstore(resultPtr, shl(248, mload(add(tablePtr, and(shr( 6, input), 0x3F)))))
               resultPtr := add(resultPtr, 1)
               mstore(resultPtr, shl(248, mload(add(tablePtr, and(        input,  0x3F)))))
               resultPtr := add(resultPtr, 1)
            }

            // padding with '='
            switch mod(mload(data), 3)
            case 1 { mstore(sub(resultPtr, 2), shl(240, 0x3d3d)) }
            case 2 { mstore(sub(resultPtr, 1), shl(248, 0x3d)) }
        }

        return result;
    }
}

contracts/ADMM.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// solhint-disable-next-line compiler-version
pragma solidity ^0.8.20;

import {IPortalToken} from "./interfaces/IPortalToken.sol";
import {IUniswapPool} from "./interfaces/IUniswapPool.sol";
import "./uniswap/core/contracts/interfaces/IUniswapV3Factory.sol";
import "./interfaces/IUniswapPositionManager.sol";
import "./interfaces/IUniswapRouter.sol";
import "./interfaces/IPoolManager.sol";
import "./interfaces/IAssetManagement.sol";
import {IAsset} from "./interfaces/IAsset.sol";
import "./interfaces/ILiquidityPool.sol";
import "./uniswap/core/contracts/libraries/TickMath.sol";
import {IADMM} from "./interfaces/IADMM.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

/**
 * @title  ADMM Contract
 * @dev Contract which manages the swap Process and liquidity accounting and management
 */
contract ADMM is IADMM {
    /**
     * @notice address of the portal Token contract
     */
    IPortalToken public tokenContract;

    /**
     * @notice address of the pool manager contract
     */
    IPoolManager public poolManager;

    /**
     * @notice address of the asset manager contract
     */
    IAssetManagement public assetManager;

    /**
     * @notice address of the liquidity pool contract
     */
    ILiquidityPool public liquidityPool;

    /**
     * @notice MULTIPLIER of all accounting assets
     */
    uint256 public constant MULTIPLIER = 1e18;

    uint256 public priceBTCtoETH = 29;

    using Strings for uint256;

    /**
     * @notice Custom onlyOwner modifier that checks ownership against the tokenContract's owner
     */
    modifier onlyOwner() {
        require(
            tokenContract.owner() == msg.sender,
            "Caller is not the token contract owner"
        );
        _;
    }

    /**
     * @notice Constructor to initialize the management contract
     * @param _tokenAddress address of the portal token contract
     * @param _assetManager address of the asset manager contract
     * @param _liquidityPool address of the liquidity pool contract
     */
    constructor(address _tokenAddress, address _assetManager, address _liquidityPool) {
        tokenContract = IPortalToken(_tokenAddress);
        assetManager = IAssetManagement(_assetManager);
        liquidityPool = ILiquidityPool(_liquidityPool);
    }

    /**
     * @notice set the address of pool manager contract and admin to the factory
     * @param _poolManager address of the pool manager contract
     */
    function setPoolManager(address _poolManager) external onlyOwner {
        poolManager = IPoolManager(_poolManager);
        poolManager.factory().addAdmin(_poolManager);
    }

    /**
     * @notice Struct to store mapping of all active swaps by id of the swap
     */
    mapping(bytes32 => Swap) public swaps;

    /**
     * @notice Struct to store mapping of all active event outputs of the swap by secretHash
     */
    mapping(bytes32 => EventOutput) public eventOutputs;

    /**
     * @notice Struct to store mapping of all active liquidityPoolId to liquidity of that liquidityPoolId
     */
    mapping(bytes32 => Liquidity[]) public liquidities;

    /**
     * @notice Struct to store mapping of all active liquidity limits with sellAsset and liquidityOwner
     */
    mapping(address => mapping(address => LiquidityLimit))
        public liquidityLimits;

    /**
     * @notice Struct to store mapping of all active matched liquidities with id
     */
    mapping(bytes32 => uint[]) public liquidityIds;

    /**
     * @notice set the liquidity limit for given owner and the asset
     * @param liquidityLimit liquidity limit details
     */
    function setLiquidityLimit(
        LiquidityLimit calldata liquidityLimit
    ) public onlyOwner {
        // Update the liquidity limit for the given pair and liquidity owner
        liquidityLimits[liquidityLimit.sellAsset][liquidityLimit.liquidityOwner] = liquidityLimit;

        // Emit an event upon successful update of the liquidity limit
        emit LiquidityLimitUpdated(liquidityLimit);
    }

    // Function to get the number of liquidity positions for a given pool ID
    function getLiquidityCount(bytes32 poolId) public view returns (uint) {
        return liquidities[poolId].length;
    }

    // Function to get a specific liquidity position for a given pool ID and index
    function getLiquidity(bytes32 poolId, uint index) public view returns (Liquidity memory) {
        require(index < liquidities[poolId].length, "Index out of bounds");
        return liquidities[poolId][index];
    }

    /**
     * @notice Liquidity Provider's liquidity committed for a concentrated position for a particular pool
     * @param liquidity liquidity position details
     */
    function commitLiquidity(Liquidity calldata liquidity) public {
        require(
            msg.sender == tokenContract.owner() ||
                msg.sender == liquidity.owner,
            "Caller is not the owner or the liquidity owner"
        );
        require(
            liquidityLimits[liquidity.sellAsset][liquidity.owner]
                .sellAmount >= liquidity.sellAmount,
            "Breached Liquidity Limit"
        );

        bytes32 liquidityPoolId = liquidityPool.retrievePoolId(
            liquidity.sellAsset,
            liquidity.buyAsset,
            liquidity.poolFee
        );
        require(liquidityPoolId != bytes32(0), "Asset pair not registered");

        uint insertIndex = _findInsertIndex(liquidityPoolId, liquidity.buyRangeLowLimit);

        _insertLiquidity(liquidityPoolId, insertIndex, liquidity);
        // _updatePool(
        //     liquidity.sellAsset,
        //     liquidity.buyAsset,
        //     liquidity.poolFee,
        //     liquidity.sellAmount
        // );
        liquidityLimits[liquidity.sellAsset][liquidity.owner]
            .sellAmount -= liquidity.sellAmount;
        emit LiquidityCommitted(liquidity);
    }

    /**
     * @dev internal helper function to find insert index for a liquidity position
     * @param liquidityPoolId  liquidityPoolId of the pool
     * @param buyRangeLowLimit sorted by liquidity position's buy low limit
     */
    function _findInsertIndex(
        bytes32 liquidityPoolId,
        uint buyRangeLowLimit
    ) internal view returns (uint) {
        uint length = liquidities[liquidityPoolId].length;
        for (uint i = 0; i < length; i++) {
            if (buyRangeLowLimit < liquidities[liquidityPoolId][i].buyRangeLowLimit) {
                return i;
            }
        }
        return length;
    }

    /**
     * @dev internal helper function to insert liquidity position
     * @param liquidityPoolId  liquidityPoolId of the pool
     * @param insertIndex index to insert the liquidity position
     * @param newLiquidity liquidity position to be inserted
     */
    function _insertLiquidity(
        bytes32 liquidityPoolId,
        uint insertIndex,
        Liquidity memory newLiquidity
    ) internal {
        liquidities[liquidityPoolId].push();
        uint length = liquidities[liquidityPoolId].length;
        if (length > 1) {
            for (uint i = length - 1; i > insertIndex; i--) {
                liquidities[liquidityPoolId][i] = liquidities[liquidityPoolId][i - 1];
            }
        }
        liquidities[liquidityPoolId][insertIndex] = newLiquidity;
    }

    /**
     * @notice function to update the pool or create a pool when pool doesn't exist
     * @param sellAsset Liquidity Provider's Sell asset address
     * @param buyAsset Liquidity Provider's Buy asset address
     * @param poolFee Fee of the pool
     * @param sellAmount Amount Liquidity Provider providing liquidity for
     */
    function _updatePool(
        address sellAsset,
        address buyAsset,
        uint poolFee,
        uint sellAmount
    ) internal {
        address pool = poolManager.getPool(
            sellAsset,
            buyAsset,
            uint24(poolFee)
        );
        if (pool == address(0)) {
            poolManager.createPool(buyAsset, sellAsset, 0);
        }
        uint160 sqrtPriceX96 = poolManager.getPoolPrice(
            buyAsset,
            sellAsset,
            0
        );
        uint price = sqrtPriceX96ToPrice(sqrtPriceX96);
        uint256 buyAmount = sellAmount * price;
        int24 tickLower = TickMath.getMinTick(10);
        int24 tickUpper = TickMath.getMaxTick(10);

        // Added sorting mechanism to ensure passing sorted token addresses to position manager of uniswap contract
        // Additional parameters associated with the token address should also be sorted
        // e.g if sellAsset address is < than buyAsset address, then sellAmount as well as sellNetworkId
        // and any other parameters should also be swaped with buy parameters.
        // In this case, if amountMin wasn't 0 for both asset, it should also be swaped
        // This is to ensure that the position manager contract of uniswap receives the parameters in the correct swap
        // This needs to be implemented for the other positionManager calls as well such as increaseLiquidity, decreaseLiquidity, etc.
        if (sellAsset > buyAsset) {
            address tmp = sellAsset;
            sellAsset = buyAsset;
            buyAsset = tmp;

            uint256 tmpAmount = sellAmount;
            sellAmount = buyAmount;
            buyAmount = tmpAmount;
        }
        poolManager.createPosition(
            IPoolManager.LpToken({
                addr: sellAsset,
                amountDesired: sellAmount,
                amountMin: 0
            }),
            IPoolManager.LpToken({
                addr: buyAsset,
                amountDesired: buyAmount,
                amountMin: 0
            }),
            address(this),
            0,
            block.timestamp + 60,
            tickLower,
            tickUpper
        );
    }

    /**
     * @notice function to remove liquidity from the pool
     * @param liquidityId liquidity position id referring to the index of the position
     * @param liquidityPoolId unique identifier of the liquidity pool
     */
    function removeLiquidity(
        uint liquidityId,
        bytes32 liquidityPoolId
    )
        public
    {
        require(liquidityPoolId != bytes32(0), "Asset pair not registered");
        require(
            liquidityId < liquidities[liquidityPoolId].length,
            "Invalid liquidityId"
        );

        // Store the liquidity to be removed for event emission
        Liquidity memory liq = liquidities[liquidityPoolId][liquidityId];
        require(
            msg.sender == tokenContract.owner() || msg.sender == liq.owner,
            "Caller is not the owner or the liquidity owner"
        );

        // Shift elements to the left to fill the gap created by the removed liquidity
        for (uint i = liquidityId; i < liquidities[liquidityPoolId].length - 1; i++) {
            liquidities[liquidityPoolId][i] = liquidities[liquidityPoolId][i + 1];
        }
        // Remove the last element after shifting
        liquidities[liquidityPoolId].pop();
        // // TODO: Remove liquidity from UniswapV3
        // poolManager.decreaseLiquidity(
        //     msg.sender,
        //     liq.sellAmount,
        //     liq.sellAmount, // TODO: why are this same?
        //     0
        // );

        liquidityLimits[liq.sellAsset][liq.owner].sellAmount += liq.sellAmount;

        // Emit the event after the liquidity has been successfully removed
        emit LiquidityRemoved(liq);
    }

    /**
     * @notice function to register a swap in Portal Chain
     * @param swap details of the swap
     */
    function createSwap(
        Swap memory swap
    ) public {
        swap.swapCreation = block.timestamp;
        swap.status = "created";
        // Record a new swap with provided parameters
        swaps[swap.id] = swap;
        // Emit an event upon successful recording of the swap
        emit SwapCreated(swap);
    }

    /**
     * @notice function to validate a swap
     * @param id unique id of the swap
     */
    function validateSwap(bytes32 id) public onlyOwner {
        Swap storage swap = swaps[id];
        require(keccak256(abi.encodePacked(swap.status)) == keccak256(abi.encodePacked("created")), "Swap is not in created state");
        swap.status = "validated";
        emit SwapValidated(swap);
    }

    /**
     * @notice function to register lightning invoice in Portal Chain
     * @param invoice lightning Invoice details for the swap
     */
    function registerInvoice(Invoice calldata invoice) public {
        Swap storage swap = swaps[invoice.id];
        EventOutput storage eventOutput = eventOutputs[swap.secretHash];
        require(keccak256(abi.encodePacked(swap.status)) == keccak256(abi.encodePacked("matched")), "Swap is not in matched state");
        swap.status = "invoiceRegistered";
        swap.secretHash = invoice.secretHash;
        eventOutput.secretHash = invoice.secretHash;
        eventOutput.invoice = invoice.invoice;
        eventOutputs[invoice.secretHash] = eventOutput;
        emit InvoiceRegistered(invoice);
    }

    // TODOO; bookkeeping of pay and settle invoice

    /**
     * @notice internal function to select a random number among all numbers from 0 to n
     * @param n Upper limit of the random number
     */
    function random(uint n) internal view returns (uint) {
        // Combine the hash of the most recently completed block with the current block's difficulty
        uint randomHash = uint(
            keccak256(
                abi.encodePacked(blockhash(block.number - 1), block.prevrandao)
            )
        );
        // Generate a random number using 'n'
        return randomHash % n;
    }

    /**
     * @notice function to match a swap with a liquidity position
     * @param id Unique Id for the swap
     */
    function matchSwap(bytes32 id) public {
        Swap storage swap = swaps[id];
        require(keccak256(abi.encodePacked(swap.status)) == keccak256(abi.encodePacked("validated")), "Swap is not in validated state");
        swap.status = "matched";
        bytes32 liquidityPoolId = swap.liquidityPoolId;
        // TODO: Ensure reflects  in UniswapV3 396,404.
        // uint buyAmount = poolManager.exactInputSingle(
        //     swap.sellAsset,
        //     swap.buyAsset,
        //     0,
        //     swap.sellAmount
        // );

        IAsset.AssetData memory sellAssetData = assetManager.retrieveAsset(swap.sellAsset);

        uint buyAmount;
        if (keccak256(abi.encodePacked(sellAssetData.symbol)) == keccak256(abi.encodePacked("BTC"))) {
            buyAmount = swap.sellAmount * priceBTCtoETH * 1e10;
        } else {
            buyAmount = swap.sellAmount/(priceBTCtoETH * 1e10);
        }
        uint price = (buyAmount * MULTIPLIER) / swap.sellAmount;
        // require(false, string(abi.encodePacked("Price:", price.toString())));
        delete liquidityIds[id];
        uint liquidityId;
        for (
            liquidityId = 0;
            liquidityId < liquidities[liquidityPoolId].length;
            liquidityId++
        ) {
            Liquidity storage liq = liquidities[liquidityPoolId][liquidityId];
            // Basic criteria for matching: sell and buy assets match and amounts are sufficient
            if (
                liq.sellAsset == swap.buyAsset &&
                liq.buyAsset == swap.sellAsset // &&
                // buyAmount  <= swap.buyAmount + swap.slippage &&
                // (price >= liq.buyRangeLowLimit * MULTIPLIER &&
                //     price <= liq.buyRangeHighLimit * MULTIPLIER)
            ) {
                // Simplified match criteria, adjust as needed
                // Match found, update liquidity and emit event
                if (liq.sellAmount >= buyAmount) {
                    liquidityIds[id].push(liquidityId);
                }
            }
        }
        if (liquidityIds[id].length > 0) {
            uint rand = random(liquidityIds[id].length);
            liquidityId = liquidityIds[id][rand];
            Liquidity storage liq = liquidities[liquidityPoolId][liquidityId];
            liq.sellAmount -= buyAmount;
            eventOutputs[swap.secretHash] = EventOutput(
                                                id,
                                                swap.secretHash,
                                                liq.owner,
                                                swap.sellAmount,
                                                buyAmount,
                                                "12345"
                                            );
            emit SwapMatched(
                id,
                liq.owner,
                swap.sellAsset,
                swap.sellAmount,
                buyAmount
            );
        } else {
            for (
                liquidityId = 0;
                liquidityId < liquidities[liquidityPoolId].length;
                liquidityId++
            ) {
                Liquidity storage liq = liquidities[liquidityPoolId][liquidityId];

                // Basic criteria for matching: sell and buy assets match and amounts are sufficient
                if (
                    liq.sellAsset == swap.buyAsset &&
                    liq.buyAsset == swap.sellAsset &&
                    buyAmount <= swap.buyAmount + swap.slippage &&
                    liq.sellAmount < buyAmount &&
                    (price > liq.buyRangeLowLimit * MULTIPLIER &&
                        price < liq.buyRangeHighLimit * MULTIPLIER)
                ) {
                    uint partialMatchedAmount = liq.sellAmount;
                    liq.sellAmount = 0;
                    eventOutputs[swap.secretHash] = EventOutput(
                                    id,
                                    swap.secretHash,
                                    liq.owner,
                                    (liq.sellAmount * MULTIPLIER) / price,
                                    partialMatchedAmount,
                                    "12345"
                                );
                    emit SwapMatched(
                        id,
                        liq.owner,
                        swap.sellAsset,
                        (liq.sellAmount * MULTIPLIER) / price,
                        partialMatchedAmount
                    );
                    swaps[id].sellAmount -=
                        (liq.sellAmount * MULTIPLIER) /
                        price;
                }
            }
        }
        delete liquidityIds[id];
    }

    /**
     * @notice function to retrieve current price of the pool
     * @param sqrtPriceX96 initial price of the pool
     */
    function sqrtPriceX96ToPrice(
        uint160 sqrtPriceX96
    ) public pure returns (uint256 price) {
        // First, convert sqrtPriceX96 to a full-precision number by squaring it.
        // Note: The squaring of the sqrtPriceX96 value increases its precision to 192 bits,
        // so it's important to handle this operation carefully to avoid overflow.
        // Since Solidity doesn't support fixed-point numbers directly, we use uint256 for high precision arithmetic.
        uint256 numerator = uint256(sqrtPriceX96) * uint256(sqrtPriceX96);

        // Next, adjust for the 96*2 bits of precision from the squaring operation above
        // by dividing by 2^192, which is equivalent to (2^96)^2.
        // We represent 2^192 as a combination of two 2^96 factors for simplicity and to fit Solidity's uint256.
        uint256 denominator = 1 << 192;

        // Calculate the price ratio. The division here is safe since the denominator is non-zero.
        // This results in a price ratio represented in a fixed-point format with 96 bits of precision.
        price = numerator / denominator;
        return price;
    }

    /**
     * @notice function to set the price of BTC in terms of ETH
     * @param price price of the asset
     */
    function setPriceBTCtoETH(uint price) public onlyOwner {
        priceBTCtoETH = price;
    }

    /**
     * @notice function to set initial sqrt price of the pool
     * @param sellAsset Liquidity Provider's Sell asset address
     * @param buyAsset Liquidity Provider's Buy asset address
     * @param poolFee Fee of the trading pool
     * @param initialSqrtPriceX96ToPrice initial price of the pool
     */
    function setInitialSqrtPrice(
        address sellAsset,
        address buyAsset,
        uint24 poolFee,
        uint160 initialSqrtPriceX96ToPrice
    )
        external
        onlyOwner
    {
        poolManager.setInitialSqrtPrice(
            sellAsset,
            buyAsset,
            poolFee,
            initialSqrtPriceX96ToPrice
        );
    }
}

