// SPDX-License-Identifier: BUSL-1.1
// solhint-disable-next-line compiler-version
pragma solidity ^0.8.20;

import {IAsset} from "./interfaces/IAsset.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @title Asset
 * The Asset contract represents an asset with specific attributes and
 * restrictions. It inherits from the ERC20 standard and includes additional
 * metadata for asset management.
 *
 * An asset is a representation of a tradable token on the portal chain, and
 * is modeled similar to an ERC-20 token, with additional metadata, namely:
 * - The name of the native blockchain.
 * - The chain-id of the native blockchain, relevant only for EVM chains.
 * - The address of the ERC20 contract asset on the native blockchain.
 * - The number of decimals used by the asset on its native blockchain.
 * - The logo of the asset as a SVG string.
 */
contract Asset is IAsset, ERC20 {
    AssetData private asset;

    /**
     * Initializes the contract setting the Asset Details
     * @param _asset Properties of the asset
     */
    constructor(
        IAsset.AssetData memory _asset
    ) ERC20(_asset.name, _asset.symbol) {
        asset = AssetData({
            id: address(this),
            name: _asset.name,
            symbol: _asset.symbol,
            logo: _asset.logo,
            blockchainId: _asset.blockchainId,
            blockchainName: _asset.blockchainName,
            blockchainAddress: _asset.blockchainAddress,
            blockchainDecimals: _asset.blockchainDecimals
        });
    }

    /**
     * Returns the address of the asset on the portal blockchain
     */
    function id() public view returns (address) {
        return asset.id;
    }

    /**
     * Returns the decimals used by the asset on its native blockchain
     */
    function decimals() public view override returns (uint8) {
        return asset.blockchainDecimals;
    }

    /**
     * Returns all the properties of the asset. This is primarily used to return
     * data in JSON format to the JavaScript layer.
     */
    function toAssetData() public view returns (AssetData memory) {
        return asset;
    }
}

contracts/AssetManagement.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// solhint-disable-next-line compiler-version
pragma solidity ^0.8.20;

import "./interfaces/IPortalToken.sol";
import {IAssetManagement} from "./interfaces/IAssetManagement.sol";
import {Asset} from "./Asset.sol";
import {IAsset} from "./interfaces/IAsset.sol";

/**
 * @title AssetManagement
 * Manages assets in the Portal ecosystem. Assets in the Portal ecosystem are
 * immutable and may not be updated or deleted once created. The contract
 * maintains a list of all assets created in the order they were created. It
 * also maintains a mapping of assets by their native blockchain name and
 * address to enable quick lookups of assets based on native blockchain details.
 */
contract AssetManagement is IAssetManagement {
    /**
     * @notice Address of the PORTAL Token contract on portal chain
     */
    IPortalToken public portalToken;

    /**
     * @notice Maintains a count of the number of assets on portal chain
     */
    uint256 private assetCount;

    /**
     * @notice List of all assets in the order they were created
     */
    Asset[] private assets;

    /**
     * @notice Mapping to check if an asset already exists
     */
    mapping(string => mapping(string => bool)) private existingAssets;

    /**
     * @notice Assets indexed by native blockchain name and address
     */
    mapping(string => mapping(string => Asset)) private assetsByNativeProps;

    /**
     * Initializes the asset management contract
     * @param _portalToken Address of the PORTAL token contract on portal chain
     */
    constructor(address _portalToken) {
        portalToken = IPortalToken(_portalToken);
    }

    /**
     * Modifier that restricts the execution of the function to only the
     * current owners of the contract. It reverts the transaction if the
     * condition is not met.
     */
    modifier onlyOwner() {
        if (msg.sender != portalToken.owner()) revert NotOwner(msg.sender);
        _;
    }

    /**
     * @inheritdoc IAssetManagement
     */
    function createAsset(IAsset.AssetData calldata _asset) external onlyOwner {
        address id = _asset.id;
        require(id == address(0), "INVALID_ASSET_ID");

        string memory blockchainName = _asset.blockchainName;
        string memory blockchainAddress = _asset.blockchainAddress;
        if (existingAssets[blockchainName][blockchainAddress]) {
            revert AssetExists(blockchainName, blockchainAddress);
        }

        Asset asset = new Asset(_asset);

        assets.push(asset);
        assetCount++;

        existingAssets[blockchainName][blockchainAddress] = true;
        assetsByNativeProps[blockchainName][blockchainAddress] = asset;

        emit AssetCreated(asset.id());
    }

    /**
     * @inheritdoc IAssetManagement
     */
    function retrieveAsset(
        address id
    ) external view returns (IAsset.AssetData memory) {
        return Asset(id).toAssetData();
    }

    /**
     * @inheritdoc IAssetManagement
     */
    function retrieveAssetByNativeProps(
        string memory blockchainName,
        string memory blockchainAddress
    ) external view returns (IAsset.AssetData memory) {
        Asset asset = assetsByNativeProps[blockchainName][blockchainAddress];
        return asset.toAssetData();
    }

    /**
     * @inheritdoc IAssetManagement
     */
    function listAssets() external view returns (IAsset.AssetData[] memory) {
        IAsset.AssetData[] memory _assets = new IAsset.AssetData[](assetCount);
        for (uint256 i = 0; i < assetCount; i++) {
            _assets[i] = assets[i].toAssetData();
        }
        return _assets;
    }
}

contracts/AuctionManagement.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// solhint-disable-next-line compiler-version
pragma solidity ^0.8.20;

import {IPortalToken} from "./interfaces/IPortalToken.sol";
import {IMultiSigWallet} from "./interfaces/IMultiSigWallet.sol";

/**
 * @title Auction Management Contract
 * @dev Contract which manages the auction process, bids, keygen ceremony
 */
contract AuctionManagement {
    /**
     * @notice current state of the Action
     */
    bool public auctionActive;

    /**
     * @notice mapping of bidders to their bids
     */
    mapping(address => uint256) public bids;

    /**
     * @notice list of bidders
     */
    address[] public bidders;

    /**
     * @notice list of primary validators
     */
    address[] public primaryValidators;

    /**
     * @notice list of secondary validators
     */
    address[] public secondaryValidators;

    /**
     * @notice number of primary validators initially set to 3
     */
    uint public numPrimaryValidators = 3; // Default value

    /**
     * @notice number of secondary validators initially set to 2
     */
    uint public numSecondaryValidators = 2; // Default value

    /**
     * @notice number of validators required to approve a transaction initially set to 2
     */
    uint public validatorThreshold = 2;

    /**
     * @notice least amount of bid to be a validator
     */
    uint public minActiveBid;

    /**
     * @notice  Portal Token contract
     */
    IPortalToken public tokenContract;

    /**
     * @dev Constructor to initialize the Auction Management contract.
     * @param _tokenAddress Address of the Portal Token contract.
     */
    constructor(address _tokenAddress) {
        tokenContract = IPortalToken(_tokenAddress);
    }

    // Custom onlyOwner modifier that checks ownership against the tokenContract's owner
    modifier onlyOwner() {
        require(
            tokenContract.owner() == msg.sender,
            "Caller is not the token contract owner"
        );
        _;
    }

    /**
     * @notice Fired when auction starts by validators.
     */
    event AuctionStarted();

    /**
     * @notice Fired when a user submits a bid.
     * @param bidder The address of the bidder
     * @param amount The amount of the bid
     */
    event BidSubmitted(address bidder, uint256 amount);

    /**
     * @notice Fired when a user increases their bid.
     * @param bidder The address of the bidder
     * @param newAmount The new amount of the bid
     */
    event BidIncreased(address bidder, uint256 newAmount);

    /**
     * @notice Fired when the auction ends by validators.
     * @param primaryValidators The list of primary validators
     * @param secondaryValidators The list of secondary validators
     */
    event AuctionEnded(
        address[] primaryValidators,
        address[] secondaryValidators
    );

    /**
     * @dev Function to adjust the number of primary validators, restricted to contract owner
     * @param _numPrimaryValidators Number of primary validators
     */
    function setNumPrimaryValidators(
        uint _numPrimaryValidators
    ) public onlyOwner {
        numPrimaryValidators = _numPrimaryValidators;
    }

    /**
     * @dev Function to adjust the number of secondary validators, restricted to contract owner
     * @param _numSecondaryValidators Number of secondary validators
     */
    function setNumSecondaryValidators(
        uint _numSecondaryValidators
    ) public onlyOwner {
        numSecondaryValidators = _numSecondaryValidators;
    }

    /**
     * @dev Function to adjust the validator threshold, restricted to contract owner
     * @param _validatorThreshold Number of validators required to approve a transaction
     */
    function setValidatorThreshold(uint _validatorThreshold) public onlyOwner {
        validatorThreshold = _validatorThreshold;
    }

    /**
     * @dev Function to start the auction, restricted to contract owner
     */
    function startAuction() public onlyOwner {
        require(!auctionActive, "Auction is already active.");
        auctionActive = true;
        delete verifiedValidators;
        minActiveBid = 0;
        emit AuctionStarted();
    }

    /**
     * @dev Function to submit a bid for the auction
     * @param amount The amount of the bid
     */
    function bid(uint256 amount) public {
        require(auctionActive, "Auction is not active.");
        require(amount > 0, "Bid amount must be greater than zero.");
        require(
            bids[msg.sender] == 0,
            "Bid already placed, use increaseBid to increase your bid"
        );
        require(
            amount <= tokenContract.getLockedTokens(msg.sender),
            "Bid amount must be less than lockedTokens"
        );
        bids[msg.sender] = amount;
        bidders.push(msg.sender);
        emit BidSubmitted(msg.sender, amount);
    }

    /**
     * @dev Function to increase an existing bid
     * @param amount The amount to increase the bid by
     */
    function increaseBid(uint256 amount) public {
        require(bids[msg.sender] > 0, "No existing bid to increase");
        require(amount > 0, "Increase amount must be greater than zero");
        uint256 newAmount = bids[msg.sender] + amount;
        require(
            newAmount <= tokenContract.getLockedTokens(msg.sender),
            "Bid amount must be less than lockedTokens"
        );

        bids[msg.sender] = newAmount;
        emit BidIncreased(msg.sender, newAmount);
    }

    /**
     * @dev Function to end the auction and select primary and secondary validators
     */
    function endAuction() public onlyOwner {
        require(auctionActive, "Auction is not active.");
        auctionActive = false;

        // Use quickSort to sort bidders based on their bids
        quickSort(0, int(bidders.length) - 1);

        // Clear previous validators
        delete primaryValidators;
        delete secondaryValidators;

        maxEligibleSecondary = 0;

        // Select top N bidders as primary validators based on numPrimaryValidators
        for (uint i = 0; i < numPrimaryValidators && i < bidders.length; i++) {
            primaryValidators.push(bidders[bidders.length - i - 1]);
        }

        // Select next M highest bidders as secondary validators based on numSecondaryValidators
        for (
            uint i = numPrimaryValidators;
            i < numPrimaryValidators + numSecondaryValidators &&
                i < bidders.length;
            i++
        ) {
            secondaryValidators.push(bidders[bidders.length - i - 1]);
        }

        if (primaryValidators.length > 0) {
            minActiveBid = bids[
                primaryValidators[primaryValidators.length - 1]
            ];
        }
        for (uint i = 0; i < bidders.length; i++) {
            delete bids[bidders[i]];
        }
        delete bidders;
        keyGenerationCompleted = false; // Reset for a new key generation process

        emit AuctionEnded(primaryValidators, secondaryValidators);
    }

    /**
     * @dev private function to sort the bidders
     * @param left index to start sorting from
     * @param right index to end sorting from
     */
    function quickSort(int left, int right) private {
        if (left < right) {
            int pivotIndex = partition(left, right);
            quickSort(left, pivotIndex - 1);
            quickSort(pivotIndex + 1, right);
        }
    }

    /**
     * @dev private function to partition the bidders
     * @param left index to start partitioning from
     * @param right index to end partitioning from
     * @return the pivot index
     */
    function partition(int left, int right) private returns (int) {
        uint pivotValue = bids[bidders[uint(right)]];
        int i = left - 1;

        for (int j = left; j < right; j++) {
            if (bids[bidders[uint(j)]] <= pivotValue) {
                i++;
                (bidders[uint(i)], bidders[uint(j)]) = (
                    bidders[uint(j)],
                    bidders[uint(i)]
                );
            }
        }

        (bidders[uint(i + 1)], bidders[uint(right)]) = (
            bidders[uint(right)],
            bidders[uint(i + 1)]
        );
        return i + 1;
    }

    /**
     * @dev returns list of products
     */
    function getPrimaryValidators() public view returns (address[] memory) {
        return primaryValidators;
    }

    /**
     * @dev returns list of secondary validators
     */
    function getSecondaryValidators() public view returns (address[] memory) {
        return secondaryValidators;
    }

    /**
     * @notice mapping to track validator participation
     */
    mapping(address => bool) public hasParticipated;

    /**
     * @notice Time by which primary validators must participate
     */
    uint256 public primaryParticipationEndTime;

    /**
     * @notice Time by which secondary validators must participate
     */
    uint256 public secondaryParticipationEndTime;

    /**
     * @notice Flag to indicate key generation completion
     */
    bool public keyGenerationCompleted = false;

    /**
     * @notice List of verified primary validators
     */
    address[] public verifiedValidators;

    /**
     * @notice maximum number of eligible secondary validators
     */
    uint256 private maxEligibleSecondary;

    // Event to indicate key generation participation
    /**
     * @notice Fired when a validator participates in key generation
     * @param validator The address of the participated validator
     */
    event KeyGenerationParticipated(address validator);

    /**
     * @notice modifier to restrict the access to primary validators
     */
    modifier onlyPrimaryValidators() {
        require(
            isPrimaryValidator(msg.sender),
            "Caller is not a primary validator"
        );
        _;
    }

    /**
     * @notice function to restrict the access to primary validators to perform keygen ceremony
     */
    function keyGen() public onlyPrimaryValidators {
        require(
            block.timestamp <= primaryParticipationEndTime,
            "Participation window has expired"
        );
        require(
            !hasParticipated[msg.sender],
            "Validator has already participated"
        );

        hasParticipated[msg.sender] = true;
        verifiedValidators.push(msg.sender);
        emit KeyGenerationParticipated(msg.sender);

        // Check if all primary validators have participated
        if (verifiedValidators.length == numPrimaryValidators) {
            keyGenerationCompleted = true;
        }
    }

    /**
     * @notice function to restrict the access to secondary validators to perform keygen ceremony after primary validators keygen completes
     */
    function keyGenSecondary() public {
        require(
            block.timestamp > primaryParticipationEndTime,
            "Primary Participation window is still open"
        );
        require(
            block.timestamp <= secondaryParticipationEndTime,
            "Participation window is closed"
        );
        require(
            !hasParticipated[msg.sender],
            "Validator has already participated"
        );
        require(!keyGenerationCompleted, "Key generation already completed");
        if (maxEligibleSecondary == 0) {
            maxEligibleSecondary =
                numPrimaryValidators -
                verifiedValidators.length;
        }
        for (
            uint i = 0;
            i < secondaryValidators.length && i < maxEligibleSecondary;
            i++
        ) {
            if (secondaryValidators[i] == msg.sender) {
                hasParticipated[msg.sender] = true;
                verifiedValidators.push(msg.sender);
                emit KeyGenerationParticipated(msg.sender);
            }
        }
        // Check if all eligible secondary validators have participated
        if (verifiedValidators.length == primaryValidators.length) {
            keyGenerationCompleted = true;
        }
    }

    /**
     * @notice  Function to start key generation process, callable by the contract owner
     * @param duration The duration of the primary key generation process
     */
    function startKeyGenerationProcess(uint256 duration) public onlyOwner {
        require(
            !keyGenerationCompleted,
            "Key generation already completed or in progress"
        );
        primaryParticipationEndTime = block.timestamp + duration;
        secondaryParticipationEndTime = block.timestamp + 2 * duration;
    }

    /**
     * @notice Function to verify primary validator address
     * @param validator The primary validator address to be verified
     */
    function isPrimaryValidator(address validator) private view returns (bool) {
        for (uint i = 0; i < primaryValidators.length; i++) {
            if (primaryValidators[i] == validator) {
                return true;
            }
        }
        return false;
    }

    /**
     * @notice Event to indicate key generation completion
     * @param status The status of key generation completion
     */
    event KeygenCompleteStatus(bool status);

    /**
     * @notice Helper function to check owners of newValidatorSet same as verifiedValidators
     * @param _newValidatorSet address of the new validator set
     */
    function isKeygenComplete(
        address _newValidatorSet
    ) public onlyOwner {
        require(keyGenerationCompleted, "Key generation not completed");
        require(
            IMultiSigWallet(_newValidatorSet).getOwners().length ==
                verifiedValidators.length,
            "Owner count mismatch"
        );
        require(
            IMultiSigWallet(_newValidatorSet).getThreshold() ==
                validatorThreshold,
            "Threshold mismatch"
        );
        for (uint i = 0; i < verifiedValidators.length; i++) {
            require(
                IMultiSigWallet(_newValidatorSet).isOwner(
                    verifiedValidators[i]
                ),
                "verified primary validator is not part of validatorSet"
            );
        }
        for (uint i = 0; i < verifiedValidators.length; i++) {
            delete hasParticipated[verifiedValidators[i]];
        }
        emit KeygenCompleteStatus(true);
    }

    /**
     * @notice Function to get the list of verified validators
     */
    function getVerifiedValidators() public view returns (address[] memory) {
        return verifiedValidators;
    }
}

contracts/interfaces/IADMM.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.20;

/**
 * @title IADMM
 * @notice Interface for the ADMM contract
 */
interface IADMM {
    /**
     * @dev Data Structure of LiquidityLimit position
     * @param liquidityOwner Address of the liquidity owner
     * @param sellAsset Liquidity Provider's Sell asset address
     * @param sellAmount Amount Liquidity Provider providing liquidity for
     */
    struct LiquidityLimit {
        address liquidityOwner;
        address sellAsset;
        uint sellAmount;
    }

    /**
     * @dev Event emitted when liquidity position is updated
     * @param liquidityLimit LiquidityLimit details
     */
    event LiquidityLimitUpdated(LiquidityLimit liquidityLimit);

    /**
     * @dev Data Structure of Liquidity position
     * @param owner Address of the liquidity owner
     * @param sellAsset Address of the asset to be sold
     * @param sellAmount Amount of the asset to be sold
     * @param buyAsset Address of the asset to be bought
     * @param poolFee Pool fee percentage
     * @param buyRangeLowLimit Lower limit of the buy range
     * @param buyRangeHighLimit Upper limit of the buy range
     */
    struct Liquidity {
        address owner;
        address sellAsset;
        uint sellAmount;
        address buyAsset;
        uint poolFee;
        uint buyRangeLowLimit;
        uint buyRangeHighLimit;
    }

    /**
     * @dev Event emitted when liquidity position is committed for a particular pool
     * @param liquidity Liquidity position to be committed
     */
    event LiquidityCommitted(Liquidity liquidity);

    /**
     * @dev Event emitted when liquidity position is removed
     * @param liquidity Liquidity position to be committed
     */
    event LiquidityRemoved(Liquidity liquidity);

    /**
     * @dev Data Structure of swap Swap
     * @param id Unique identifier of the swap
     * @param liquidityPoolId Unique identifier of the pair
     * @param secretHash Hash of the secret
     * @param sellAsset Address of the asset to be sold
     * @param sellAmount Amount of the asset to be sold
     * @param buyAsset Address of the asset to be bought
     * @param buyAmount Amount of the asset to be bought
     * @param slippage Slippage percentage in bps
     * @param swapCreation Timestamp of the swap creation
     * @param swapOwner Address of the swap owner
     * @param buyId Unique identifier of the trader in counterparty chain
     * @param status Status of the swap
     */
    struct Swap {
        bytes32 id;
        bytes32 liquidityPoolId;
        bytes32 secretHash;
        address sellAsset;
        uint sellAmount;
        address buyAsset;
        uint buyAmount;
        uint slippage;
        uint swapCreation;
        address swapOwner; 
        string buyId;
        string status;       
    }

    struct EventOutput {
        bytes32 id;
        bytes32 secretHash;
        address matchedLp;
        uint matchedSellAmount;
        uint matchedBuyAmount;
        string invoice;
    }


    /**
     * @dev Event emitted when a swap is registered
     * @param swap swap details
     */
    event SwapCreated(Swap swap);

    /**
     * @dev Event emitted when a swap is registered
     * @param swap swap details
     */
    event SwapValidated(Swap swap);

    /**
     * @dev Event emitted when a swap is matched
     * @param id Unique identifier of the swap
     * @param liquidityOwner Address of the liquidity owner
     * @param sellAsset asset address to be sold
     * @param matchedSellAmount Matched amount of the asset to be sold
     * @param matchedBuyAmount Matched amount of the asset to be bought
     */
    event SwapMatched(
        bytes32 indexed id,
        address indexed liquidityOwner,
        address sellAsset,
        uint matchedSellAmount,
        uint matchedBuyAmount
    );

    /**
     * @dev Data Structure of Invoice
     * @param id Unique identifier of the swap
     * @param secretHash Hash of the secret
     * @param invoice Invoice of the swap
     */
    struct Invoice {
        bytes32 id;
        bytes32 secretHash;
        uint amount;
        string invoice;
    }

    /**
     * @dev Event emitted when an invoice is successfully registered
     * @param invoice invoice of the swap which is registered on portal chain
     */
    event InvoiceRegistered(Invoice invoice);
}

