contracts/interfaces/IAssetManagement.so.content:
// SPDX-License-Identifier: BUSL-1.1
// solhint-disable-next-line compiler-version
pragma solidity ^0.8.20;

import "./IAsset.sol";
import "../Asset.sol";

/**
 * @title IAssetManagement
 * Interface to manage assets in the Portal ecosystem
 */
interface IAssetManagement {
    /**
     * Creates a new asset.
     * NOTE: This method is only callable by the owner of the contract.
     */
    function createAsset(IAsset.AssetData calldata asset) external;

    /**
     * Retrieves the specified asset
     * @param id The address of the asset on the portal chain
     * @return The asset whose id was provided
     */
    function retrieveAsset(
        address id
    ) external view returns (IAsset.AssetData memory);

    /**
     * Retrieves an asset using native blockchain properties
     * @param blockchainName Name of the native blockchain
     * @param blockchainAddress Address of the asset on the native blockchain
     * @return The asset whose native blockchain properties were provided
     */
    function retrieveAssetByNativeProps(
        string memory blockchainName,
        string memory blockchainAddress
    ) external view returns (IAsset.AssetData memory);

    /**
     * Lists all assets on the portal chain in the order they were created
     * @return The list of all assets in the order they were created
     */
    function listAssets() external view returns (IAsset.AssetData[] memory);

    /**
     * Fires when a new asset is created
     * @param id The address of the asset on the portal chain
     */
    event AssetCreated(address indexed id);

    /**
     * Thrown when the caller is not the owner of the contract
     * @param sender The address of the account calling the function
     */
    error NotOwner(address sender);

    /**
     * Thrown when an asset has already been created
     * @param blockchainName Name of the native blockchain
     * @param blockchainAddress Address of the asset on the native blockchain
     */
    error AssetExists(string blockchainName, string blockchainAddress);
}

contracts/interfaces/ILiquidityPool.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// solhint-disable-next-line compiler-version
pragma solidity ^0.8.20;

/**
 * @title ILiquidityPool
 * @dev Interface for the LiquidityPool contract that defines functions for managing liquidity pools.
 */
interface ILiquidityPool {
      /**
     * @dev Data Structure of AssetPair
     * @param id The unique identifier of the pool
     * @param baseAsset The address of the base asset
     * @param quoteAsset The address of the quote asset
     * @param poolFee The pool fee
     * @param minOrderSize The minimum order size for the base asset
     * @param maxOrderSize The maximum order size for the base asset
     */
    struct LiquidityPool {
        bytes32 id;
        address baseAsset;
        address quoteAsset;
        uint poolFee;
        uint minOrderSize;
        uint maxOrderSize;
    }

    /**
     * @dev creation of a liquidity pool for new pair and emit the event
     * @param pool Properties of the liquidity pool
     */
    function createLiquidityPool(LiquidityPool memory pool) external;

    /**
     * @notice function to retrieve liquidityPoolId from pair details
     * @param sellAsset Trader's sell asset address
     * @param buyAsset Trader's buy asset address
     * @param poolFee Fee of the trading pool
     * @return liquidityPoolId liquidityPoolId of the pool
     */
    function retrievePoolId(
        address sellAsset,
        address buyAsset,
        uint poolFee
    ) external view returns (bytes32);

    /**
     * Retrieves a liquidity pool by its unique identifier
     * @param id The unique identifier of the liquidity pool
     * @return The liquidity pool whose id was provided
     */
    function retrievePool(bytes32 id) external view returns (LiquidityPool memory);

    /**
     * Lists all liquidity pools on the portal chain
     * @return The list of all liquidity pools
     */
    function listPools() external view returns (LiquidityPool[] memory);

    /**
     * @dev Event emitted after a pair is registered
     * @param liquidityPoolId The liquidityPoolId of the pair
     */
    event LiquidityPoolCreated(
        bytes32 liquidityPoolId
    );

    // Custom errors
    /**
     * @dev Error that is thrown when the caller is not the owner of the contract
     * @param sender the address of the sender
     */
    error NotOwner(address sender);

}

contracts/interfaces/IMultiSigWallet.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// solhint-disable-next-line compiler-version
pragma solidity ^0.8.20;

/**
 * @title IMultiSigWallet
 * @notice Interface for the MultiSigWallet contract deployed on the Ethereum network
 */
interface IMultiSigWallet {
    function isOwner(address owner) external view returns (bool);
    function getThreshold() external view returns (uint256);
    function getOwners() external view returns (address[] memory);
}

contracts/interfaces/IPoolManager.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// solhint-disable-next-line compiler-version
pragma solidity ^0.8.20;
import "./IUniswapPositionManager.sol";
import "../uniswap/core/contracts/interfaces/IUniswapV3Factory.sol";

/**
 * @title IPoolManager
 * @dev The  Pool Manager interface defines the functions for managing liquidity pools and positions on Uniswap V3.
 * @notice This interface is used to interact with the  Pool Manager contract.
 */
interface IPoolManager {
    /**
     * @dev Struct to represent Uniswap parameters.
     * @param admin The address of the admin.
     * @param factory The address of the Uniswap V3 factory.
     * @param nft The address of the NFT.
     * @param router The address of the router.
     */
    struct UniswapParams {
        address admin;
        address factory;
        address nft;
        address router;
    }

    /**
     * @dev Struct to represent a liquidity position.
     * @param tokenId The ID of the liquidity token.
     * @param liquidity The amount of liquidity.
     */
    struct LiquidityPosition {
        uint tokenId;
        uint128 liquidity;
    }

    /**
     * @dev Struct to represent an LP token.
     * @param addr The address of the LP token.
     * @param amountDesired The desired amount of the LP token.
     * @param amountMin The minimum amount of the LP token.
     */

    struct LpToken {
        address addr;
        uint256 amountDesired;
        uint256 amountMin;
    }

    /**
     * @dev Returns the address of the pool for the given tokens and fee.
     * @param tokenA The address of token A.
     * @param tokenB The address of token B.
     * @param fee The fee of the pool.
     * @return pool The address of the pool.
     */
    function getPool(
        address tokenA,
        address tokenB,
        uint24 fee
    ) external view returns (address pool);

    /**
     * @dev Creates a new pool for the given tokens and fee.
     * @param sellAsset The address of the sell asset.
     * @param buyAsset The address of the buy asset.
     * @param poolFee The fee of the pool.
     */
    function createPool(
        address sellAsset,
        address buyAsset,
        uint24 poolFee
    ) external;

    /**
     * @dev Creates a new liquidity position for the given assets and owner.
     * @param sellAsset The LP token for the sell asset.
     * @param buyAsset The LP token for the buy asset.
     * @param owner The address of the owner.
     * @param poolFee The fee of the pool.
     * @param deadline The deadline for the transaction.
     * @param tickLower The lower tick for the position.
     * @param tickUpper The upper tick for the position.
     */
    function createPosition(
        LpToken calldata sellAsset,
        LpToken calldata buyAsset,
        address owner,
        uint24 poolFee,
        uint deadline,
        int24 tickLower,
        int24 tickUpper
    ) external;

    /**
     * @dev Increases the liquidity of the given position.
     * @param tokenId The ID of the liquidity token.
     * @param sellAsset The LP token for the sell asset.
     * @param buyAsset The LP token for the buy asset.
     * @param deadline The deadline for the transaction.
     */
    function increaseLiquidity(
        uint256 tokenId,
        LpToken calldata sellAsset,
        LpToken calldata buyAsset,
        uint deadline
    ) external;

    /**
     * @dev Decreases the liquidity of the given position.
     * @param owner The address of the owner.
     * @param amount0Min The minimum amount of token 0.
     * @param amount1Min The minimum amount of token 1.
     * @param deadline The deadline for the transaction.
     */
    function decreaseLiquidity(
        address owner,
        uint256 amount0Min,
        uint256 amount1Min,
        uint256 deadline
    ) external;

    /**
     * @dev Executes an exact input single swap.
     * @param tokenIn The address of the input token.
     * @param tokenOut The address of the output token.
     * @param fee The fee of the pool.
     * @param amountIn The amount of the input token.
     * @return amountOut The amount of the output token.
     */
    function exactInputSingle(
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountIn
    ) external payable returns (uint256 amountOut);

    /**
     * @dev Returns the address of the Uniswap V3 factory.
     * @return pool The address of the factory.
     */
    function factory() external view returns (IUniswapV3Factory pool);

    /**
     * @dev Sets the initial sqrt price for the given pool.
     * @param sellToken The address of the sell token.
     * @param buyToken The address of the buy token.
     * @param fee The fee of the pool.
     * @param sqrtPriceX96 The initial sqrt price.
     */
    function setInitialSqrtPrice(
        address sellToken,
        address buyToken,
        uint24 fee,
        uint160 sqrtPriceX96
    ) external;

    /**
     * @dev Returns the current sqrt price for the given pool.
     * @param tokenA The address of token A.
     * @param tokenB The address of token B.
     * @param fee The fee of the pool.
     * @return sqrtPriceX96 The current sqrt price.
     */
    function getPoolPrice(
        address tokenA,
        address tokenB,
        uint24 fee
    ) external view returns (uint160 sqrtPriceX96);

    /**
     * @dev Error thrown when no enabled tick spacing is found for the given pool fee.
     * @param poolFee The pool fee for which no enabled tick spacing was found.
     */
    error NoEnabledTickSpacing(uint poolFee);

    /**
     * @dev Error thrown when no admin is found for the given address.
     * @param sender The address for which no admin was found.
     */
    error NoAdmin(address sender);

    /**
     * @dev Error thrown when no initial sqrt price is set for the given sell and buy assets.
     * @param sellAsset The address of the sell asset.
     * @param buyAsset The address of the buy asset.
     * @param fee The fee of the pool.
     */
    error NoInitialSqrtPrice(address sellAsset, address buyAsset, uint160 fee);
}

