contracts/PoolManager.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// solhint-disable-next-line compiler-version
pragma solidity ^0.8.20;
import {IUniswapPool} from "./interfaces/IUniswapPool.sol";
import "./uniswap/core/contracts/interfaces/IUniswapV3Factory.sol";
import "./interfaces/IUniswapPositionManager.sol";
import "./uniswap/periphery/interfaces/ISwapRouter.sol";
import "./uniswap/core/contracts/libraries/TickMath.sol";
import "./interfaces/IPoolManager.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

/**
 * @title PoolManager
 * @dev The  Pool Manager contract manages liquidity pools and positions on Uniswap V3.
 * @notice This contract is used to interact with Uniswap V3 pools and positions.
 */
contract PoolManager is IPoolManager {
    /**
     * @dev The Uniswap V3 factory contract.
     */
    IUniswapV3Factory public override factory;

    /**
     * @dev The Uniswap position manager contract.
     */
    IUniswapPositionManager public positionManager;

    /**
     * @dev The swap router contract.
     */
    ISwapRouter public router;

    /**
     * @dev The admin address.
     */
    address public admin;

    using EnumerableSet for *;

    /**
     * @dev Mapping of token IDs to EnumerableSet of uints.
     */
    mapping(address => EnumerableSet.UintSet) private tokenIds;

    /**
     * @dev Mapping of token IDs to liquidity amounts.
     */
    mapping(uint => uint128) public liquidities;

    /**
     * @dev Mapping of pool IDs to initial sqrt prices.
     */
    mapping(bytes32 => uint160) public initialSqrtPrice;

    /**
     * @dev Minimum swap out amount.
     */
    uint public constant MIN_SWAP_OUT_AMOUNT = 1;

    /**
     * @dev Modifier to check if the caller is the admin.
     */
    modifier onlyAdmin() {
        if (admin != msg.sender) {
            revert NoAdmin(msg.sender);
        }
        _;
    }

    /**
     * @dev Constructor to initialize the contract.
     * @param _uniswapParams The Uniswap parameters.
     */
    constructor(UniswapParams memory _uniswapParams) {
        admin = _uniswapParams.admin;
        factory = IUniswapV3Factory(_uniswapParams.factory);
        positionManager = IUniswapPositionManager(_uniswapParams.nft);
        router = ISwapRouter(_uniswapParams.router);
    }

    /**
     * @dev Returns the address of the pool for the given tokens and fee.
     * @param tokenA The address of token A.
     * @param tokenB The address of token B.
     * @param fee The fee of the pool.
     * @return pool The address of the pool.
     */

    function getPool(
        address tokenA,
        address tokenB,
        uint24 fee
    ) external view returns (address pool) {
        return factory.getPool(tokenA, tokenB, fee);
    }

    /**
     * @dev Creates a new pool for the given tokens and fee.
     * @param sellAsset The address of the sell asset.
     * @param buyAsset The address of the buy asset.
     * @param poolFee The fee of the pool.
     */
    function createPool(
        address sellAsset,
        address buyAsset,
        uint24 poolFee
    ) external onlyAdmin {
        // Sort the tokens for the uniswap position manager.
        // Sorted tokens addresses are not used everywhere, just for position manager call: createAndInitializePoolIfNecessary
        (address token0, address token1) = sellAsset < buyAsset
            ? (sellAsset, buyAsset)
            : (buyAsset, sellAsset);
        // Initialize Uniswap contracts
        int24 tickSpacing = factory.feeAmountTickSpacing(uint24(poolFee));
        if (tickSpacing == 0) {
            factory.enableFeeAmount(uint24(poolFee), 10);
        }

        // create uniswap pool. because we are using different fee according to the pool.
        uint160 sqrtPriceX96 = initialSqrtPrice[
            _generatePoolId(sellAsset, buyAsset, poolFee)
        ];
        if (sqrtPriceX96 == 0) {
            revert NoInitialSqrtPrice(sellAsset, buyAsset, poolFee);
        }
        positionManager.createAndInitializePoolIfNecessary(
            token0,
            token1,
            poolFee,
            sqrtPriceX96
        );
    }

    /**
     * @dev Creates a new liquidity position for the given assets and owner.
     * @param sellAsset The LP token for the sell asset.
     * @param buyAsset The LP token for the buy asset.
     * @param owner The address of the owner.
     * @param poolFee The fee of the pool.
     * @param deadline The deadline for the transaction.
     * @param tickLower The lower tick for the position.
     * @param tickUpper The upper tick for the position.
     */
    function createPosition(
        LpToken calldata sellAsset,
        LpToken calldata buyAsset,
        address owner,
        uint24 poolFee,
        uint deadline,
        int24 tickLower,
        int24 tickUpper
    ) external onlyAdmin {
        _verifyTickSpacing(poolFee);
        _createPosition(
            sellAsset,
            buyAsset,
            owner,
            poolFee,
            deadline,
            tickLower,
            tickUpper
        );
    }

    /**
     * @dev Verifies the tick spacing for a given fee tier.
     * @param poolFee Fee tier of the pool.
     */
    function _verifyTickSpacing(uint24 poolFee) private view {
        int24 tickSpacing = factory.feeAmountTickSpacing(poolFee);
        if (tickSpacing == 0) {
            revert NoEnabledTickSpacing(poolFee);
        }
    }

    /**
     * @dev Creates a new liquidity position in a Uniswap pool.
     * @param sellAsset Details of the sell asset.
     * @param buyAsset Details of the buy asset.
     * @param owner Address of the position owner.
     * @param poolFee Fee tier of the pool.
     * @param deadline Deadline for the transaction.
     * @param tickLower Lower tick of the position.
     * @param tickUpper Upper tick of the position.
     */
    function _createPosition(
        LpToken calldata sellAsset,
        LpToken calldata buyAsset,
        address owner,
        uint24 poolFee,
        uint deadline,
        int24 tickLower,
        int24 tickUpper
    ) private {
        // Functionality to create position
        factory.addAdmin(address(positionManager));
        IUniswapPositionManager.MintParams
            memory params = IUniswapPositionManager.MintParams(
                sellAsset.addr,
                buyAsset.addr,
                poolFee,
                tickLower,
                tickUpper,
                sellAsset.amountDesired,
                buyAsset.amountDesired,
                sellAsset.amountMin,
                buyAsset.amountMin,
                owner,
                deadline
            );
        (uint tokenId, uint128 liquidity, , ) = positionManager.mint(params);
        // remove authority to mint token to position manager.
        factory.removeAdmin(address(positionManager));
        tokenIds[owner].add(tokenId);
        liquidities[tokenId] = liquidity;
    }

    /**
     * @dev Increases liquidity for a given position.
     * @param tokenId Token ID of the position.
     * @param sellAsset Details of the sell asset.
     * @param buyAsset Details of the buy asset.
     * @param deadline Deadline for the transaction.
     */
    function increaseLiquidity(
        uint256 tokenId,
        LpToken calldata sellAsset,
        LpToken calldata buyAsset,
        uint deadline
    ) external onlyAdmin {
        IUniswapPositionManager.IncreaseLiquidityParams
            memory params = IUniswapPositionManager.IncreaseLiquidityParams(
                tokenId,
                sellAsset.amountDesired,
                buyAsset.amountDesired,
                sellAsset.amountMin,
                buyAsset.amountMin,
                deadline
            );
        positionManager.increaseLiquidity(params);
    }

    // function getTokenAmounts(uint tokenId) private view returns (uint256 token0, uint256 token1) {
    //     (,,,,,,,,,token0, token1,) = positionManager.positions(tokenId);
    // }
    // // Assume, one user don't try to make 100+ liquidity position to same pool.
    /**
     * @dev Decreases liquidity for a given position.
     * @param owner Address of the position owner.
     * @param amount0Min Minimum amount of token0.
     * @param amount1Min Minimum amount of token1.
     * @param deadline Deadline for the transaction.
     */
    function decreaseLiquidity(
        address owner,
        uint256 amount0Min,
        uint256 amount1Min,
        uint256 deadline
    ) external onlyAdmin {
        EnumerableSet.UintSet storage ids = tokenIds[owner];
        IUniswapPositionManager.DecreaseLiquidityParams memory params;
        for (uint i = 0; i < ids.length(); i++) {
            uint tokenId = ids.at(i);
            uint liquidity = liquidities[tokenId];
            //(uint256 token0, uint256 token1) = getTokenAmounts(tokenId);
            uint256 token0 = 0;
            uint256 token1 = 1;

            if (amount0Min < token0 || amount1Min < token1) {
                params = IUniswapPositionManager.DecreaseLiquidityParams(
                    tokenId,
                    uint128(liquidity),
                    amount0Min,
                    amount1Min,
                    deadline
                );
                positionManager.decreaseLiquidity(params);
            } else {
                amount0Min -= token0;
                amount1Min -= token1;
                params = IUniswapPositionManager.DecreaseLiquidityParams(
                    tokenId,
                    uint128(liquidity),
                    token0,
                    token1,
                    deadline
                );
                positionManager.decreaseLiquidity(params);
            }
        }
    }

    /**
     * @dev Executes a swap operation with exact input amount.
     * @param tokenIn Address of the input token.
     * @param tokenOut Address of the output token.
     * @param fee Fee tier of the pool.
     * @param amountIn Input token amount.
     * @return amountOut Output token amount.
     */
    function exactInputSingle(
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountIn
    ) external payable returns (uint256 amountOut) {
        uint160 priceSqrtLimit = 0;
        if (uint160(tokenIn) < uint160(tokenOut)) {
            priceSqrtLimit = 4295128740; // This is a large integer, fits within uint256
        } else {
            priceSqrtLimit = 1461446703485210103287273052203988822378723970341;
        }
        ISwapRouter.ExactInputSingleParams memory swapParams = ISwapRouter
            .ExactInputSingleParams(
                tokenIn,
                tokenOut,
                fee,
                msg.sender,
                block.timestamp,
                amountIn,
                MIN_SWAP_OUT_AMOUNT,
                priceSqrtLimit
            );
        if (!factory.isAdmin(address(router))) {
            factory.addAdmin(address(router));
        }
        amountOut = router.exactInputSingle(swapParams);
        factory.removeAdmin(address(router));
    }

    /**
     * @dev Sets the initial sqrt price for a pool.
     * @param sellToken Address of the sell token.
     * @param buyToken Address of the buy token.
     * @param fee Fee tier of the pool.
     * @param sqrtPriceX96 Initial sqrt price of the pool.
     */
    function setInitialSqrtPrice(
        address sellToken,
        address buyToken,
        uint24 fee,
        uint160 sqrtPriceX96
    ) external onlyAdmin {
        initialSqrtPrice[
            _generatePoolId(sellToken, buyToken, fee)
        ] = sqrtPriceX96;
    }

    /**
     * @dev Generates a unique pool ID based on token addresses and fee tier.
     * @param sellToken Address of the sell token.
     * @param buyToken Address of the buy token.
     * @param fee Fee tier of the pool.
     * @return Pool ID.
     */
    function _generatePoolId(
        address sellToken,
        address buyToken,
        uint24 fee
    ) private pure returns (bytes32) {
        return keccak256(abi.encodePacked(sellToken, buyToken, fee));
    }

    /**
     * @dev Gets the price of a pool.
     * @param tokenA Address of the first token.
     * @param tokenB Address of the second token.
     * @param fee Fee tier of the pool.
     * @return sqrtPriceX96 Current sqrt price of the pool.
     */
    function getPoolPrice(
        address tokenA,
        address tokenB,
        uint24 fee
    ) external view returns (uint160 sqrtPriceX96) {
        address pool = factory.getPool(tokenA, tokenB, fee);
        (sqrtPriceX96, , , , , , ) = IUniswapPool(pool).slot0();
    }

    // function logPositions(address owner) public view {
    //     EnumerableSet.UintSet storage ids = tokenIds[owner];
    //     for (uint i = 0; i < ids.length(); i++) {
    //         uint tokenId = ids.at(i);
