contracts/PortalToken.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// solhint-disable-next-line compiler-version
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "./interfaces/IPortalToken.sol";

/**
 * @title PORTAL Token
 * Implementation of the PORTAL token, an ERC20 token with burn and permit
 * capabilities. The token has a feature for locked tokens and ownership
 * transitions.
 */
contract PortalToken is ERC20Burnable, ERC20Permit, IPortalToken {
    /**
     * The total initial supply of PORTAL tokens, set to 70 million.
     */
    uint256 public constant INITIAL_SUPPLY = 70_000_000 * 1e18;

    /**
     * The duration of each epoch, set to 30 days.
     */
    uint256 public constant EPOCH_DURATION = 30 days;

    /**
     * The timestamp of the last ownership transition.
     */
    uint256 public lastOwnershipTransition;

    /**
     * The amount that can be minted in each ownership transition.
     */
    uint256 public mintableAmount;

    /**
     * The address of the Portal Foundation.
     */
    address public immutable portalFoundation;

    /**
     * The address of the owner of the contract.
     */
    address public owner;

    /**
     * Mapping from user address to their locked token information.
     */
    mapping(address => uint) public lockedTokens;

    /**
     * Fired when a user locks tokens.
     * @param user The user whose tokens were locked
     * @param amount The amount of tokens locked
     */
    event TokensLocked(address indexed user, uint256 amount);

    /**
     * Fired after a user loses the bidding process.
     * @param user The user whose tokens were unlocked
     * @param amount The amount of the tokens unlocked
     */
    event TokensUnlocked(address indexed user, uint256 amount);

    /**
     * Fired upon transferring tokens from a user to the owner.
     * @param from Address of the user from whom the tokens will be transferred.
     * @param owner Address of the owner to whom the tokens will be transferred.
     * @param amount Amount of tokens to be transferred.
     */
    event TransferFromUserToOwner(address from, address owner, uint256 amount);

    /**
     * Sets the initial values for the PORTAL token.
     * @param _portalFoundation Address of the Portal Foundation.
     */
    constructor(
        address _portalFoundation
    ) ERC20("PORTAL", "xPORT") ERC20Permit("PORTAL") {
        require(_portalFoundation != address(0), "EBADADDRESS");
        // save the address of the portal foundation
        portalFoundation = _portalFoundation;

        // mint initial supply to portal foundation
        _mint(_portalFoundation, INITIAL_SUPPLY);

        // 10% of the initial supply is mintable at the start
        mintableAmount = INITIAL_SUPPLY / 10;

        // TODO: fix this to use block heights as opposed to timestamps
        lastOwnershipTransition = block.timestamp;

        // set the initial owner to the Portal Foundation
        owner = _portalFoundation;
    }

    /**
     * Transfers ownership of the PORTAL Token contract, with added minting
     * logic. Can only be called by the current owner.
     * @param _newOwner Address of the new owner.
     */
    function transferOwnership(address _newOwner) public {
        require(msg.sender == owner, "ENOTAUTHORIZED");

        uint256 minDuration = lastOwnershipTransition + EPOCH_DURATION - 2 days;
        uint256 maxDuration = lastOwnershipTransition + EPOCH_DURATION + 1 days;
        require(
            block.timestamp >= minDuration && block.timestamp <= maxDuration,
            "EEXPIRED"
        );

        //mint rewards to current owner
        _mint(owner, mintableAmount);

        // slash mintable rewards for next epoch by 5%
        mintableAmount -= mintableAmount / 20;

        // ownership transition
        owner = _newOwner;

        lastOwnershipTransition = block.timestamp;
    }

    /**
     * Reverts ownership to the Portal Foundation after a specific period.
     */
    function revertOwnershipToFoundation() public {
        require(
            block.timestamp > lastOwnershipTransition + EPOCH_DURATION + 1 days,
            "ENOTEXPIRED"
        );
        require(msg.sender == portalFoundation, "ENOTAUTHORIZED");

        //mint rewards to current owner
        _mint(owner, mintableAmount);

        // slash mintable rewards for next epoch by 5%
        mintableAmount -= mintableAmount / 20;

        // ownership transition
        owner = portalFoundation;

        lastOwnershipTransition = block.timestamp;
    }

    /**
     * Allows a user to lock their tokens until a specified time.
     * @param _amount Amount of tokens to be locked.
     */
    function lockTokens(uint256 _amount) public {
        require(balanceOf(msg.sender) >= _amount, "EINSUFFICIENTBALANCE");

        _transfer(msg.sender, address(this), _amount);

        lockedTokens[msg.sender] += _amount;

        emit TokensLocked(msg.sender, _amount);
    }

    /**
     * Allows a user or the owner to unlock a specified amount of tokens.
     * @param _user Address of the user whose tokens are to be unlocked.
     * @param _amount Amount of tokens to be unlocked.
     */
    function unlockTokens(address _user, uint256 _amount) public {
        require(_amount > 0, "EINVALID");
        require(lockedTokens[_user] >= _amount, "EINSUFFICIENTBALANCE");
        require(msg.sender == owner, "ENOTAUTHORIZED");

        lockedTokens[_user] -= _amount;

        _transfer(address(this), _user, _amount);

        emit TokensUnlocked(_user, _amount);
    }

    /**
     * Transfers tokens from a user back to the owner. Can only be called
     * by the owner.
     * @param _from Address of the user from whom the tokens will be transferred
     * @param _amount Amount of tokens to be transferred.
     */
    function transferFromUserToOwner(address _from, uint256 _amount) public {
        require(msg.sender == owner, "ENOTAUTHORIZED");
        _transfer(_from, owner, _amount);

        //Emit an event for transparency and tracking
        emit TransferFromUserToOwner(_from, owner, _amount);
    }

    /**
     * Returns the amount of locked tokens for a user.
     * @param _user Address of the user whose locked tokens are to be retrieved
     */
    function getLockedTokens(address _user) public view returns (uint) {
        return lockedTokens[_user];
    }
}

